$(function(){
	$(".hc_tag li").not(".more").children("a").bind("click",
	    function(){
		var a=$(".hc_tag").children("dd"),
		b=a.find("dl").eq(0).find("a.current").text(),
		c=a.find("dl").eq(1).find("a.current").text(),
		d=a.find("dl").eq(2).find("a.current").text(),
		e=$(this).text();
		companyListvue.city=e,
		companyListvue.fz=b,
		companyListvue.hy=c,
		$("#fs").val(b),
		$("#ifs").val(c),
		$("#ol").val(d),
		$("#city").val(e) })
		
		,$("#box_expectCity dd span").click(
				function(){
					var a=$(".hc_tag").children("dd"),b=a.find("dl").eq(0).find("a.current").text(),
					c=a.find("dl").eq(1).find("a.current").text(),
					d=a.find("dl").eq(2).find("a.current").text(),
					e=$(this).text();
					companyListvue.city=e,
					companyListvue.fz=b,
					companyListvue.hy=c,
					$("#fs").val(b),
					$("#ifs").val(c),
					$("#ol").val(d),
					$("#city").val(e) })
					
		,$(".hc_tag dd a").bind("click",function(){
			var a,b,c,d,e;
			$(this).hasClass("current")?$(this).removeClass("current"):($(this).siblings().removeClass("current"),
					$(this).addClass("current")),
					a=$(".hc_tag").children("dd"),
					b=a.find("dl").eq(0).find("a.current").text(),
					c=a.find("dl").eq(1).find("a.current").text(),
					d=a.find("dl").eq(2).find("a.current").text(),
					e=$(".hc_tag .workplace a.current").text(),
					companyListvue.city=e,
					companyListvue.fz=b,
					companyListvue.hy=c,
					$("#fs").val(b),
					$("#ifs").val(c),
					$("#ol").val(d),
					$("#city").val(e) })
		,$(".hc_tag li a").bind("click",function(){
			var a,b,c,d,e;
			$(this).hasClass("current")?$(this).removeClass("current"):($(this).parents('li').siblings().children().removeClass("current"),
					$(this).addClass("current")),
					a=$(".hc_tag").children("dd"),
					b=a.find("dl").eq(0).find("a.current").text(),
					c=a.find("dl").eq(1).find("a.current").text(),
					d=a.find("dl").eq(2).find("a.current").text(),
					e=$(".hc_tag .workplace a.current").text(),
					companyListvue.city=e,
					companyListvue.fz=b,
					companyListvue.hy=c,
					$("#fs").val(b),
					$("#ifs").val(c),
					$("#ol").val(d),
					$("#city").val(e) })
});




